import React, { useState, useEffect } from 'react'
import './App.css'

function App() {
    //declare variable to hold response data
    const [stock, setStock] = useState('undefined');
    //make button counter
    const [counter, setCounter] = useState(0);
    //make button change text
    const [word, setWord] = useState('Btn has not been pressed yet');
    //safety first?
    const [getprice, setGetPrice] = useState(false);


    useEffect(() => {
        if (getprice == true) {
            console.log("useEffect triggered")
            //send http request get to backend /stock
            fetch("/stock", {
                //set http request parameters
                method: 'GET',
                mode: 'cors',
                cache: 'no-cache',
                credentials: 'same-origin',
                headers: {
                    'Content-Type': 'application/json'
                },
                redirect: 'follow',
                referrerPolicy: 'no-referrer'
                })
            .then(
                //parse response in json()
                res => res.json()
            )
            .then(
                stock => {
                    //write into var
                    setStock(stock)
                    //print to console
                    console.log(stock)
                    setSafety(false)
                }
            )
            .catch((error) => {
                console.log(error)
            })
        }
    //empty array to only fetch once [] or trigger on value
    }, [safety])


    return (
        <div className="App">
            <h1>Stock</h1>
                {/*btn -->*/}
                <button onClick={() => {
                        setCounter(counter + 1);
                        setSafety(true);
                }}>Fetch price</button>

                {/*check that response has some sort of value*/}
                {(typeof stock === 'undefined') ? (
                    //err msg
                    <p>
                        waiting for Flask backend response Stock...
                    </p>
                ) : (
                    //"print" value of response.json
                    <p>
                        <br></br><br></br>
                        regularMarketPrice of stock "TSLA": { JSON.stringify(stock) } USD.
                        <br></br><br></br>
                    </p>
                )}

                {/*btn -->*/}
                <button onClick={() => {
                    setWord('Btn has been pressed');
                    setCounter(counter + 1)
                }}>Change me</button>
                <br></br><br></br>
                {word}
                <br></br><br></br>
                You have clicked the buttons {counter} times.
        </div>
    )
}

export default App
